'use strict';

module.exports = function(grunt) {

  grunt.initConfig({
    jshint: {
      options: {
        jshintrc: '.jshintrc',
      },
      sass: {     options: {        sourceMap: true      },      dist: {        files: {          'css/main.css': 'sass/main.sass'        }      }    },      all: ['*.js', 'test/*.js'],
    },
    nodeunit: {
      util: ['test/index.js']
    },
    watch: {
      all: {
        files: ['<%= jshint.all %>'],
        tasks: ['test'],
      },
    },
  });

  grunt.loadNpmTasks('grunt-contrib-jshint');
  grunt.loadNpmTasks('grunt-contrib-nodeunit');
  grunt.loadNpmTasks('grunt-contrib-watch');

  grunt.registerTask('test', ['jshint', 'nodeunit']);
  grunt.loadNpmTasks('grunt-sass');   grunt.registerTask('default', ['sass']);  grunt.registerTask('default', ['test', 'watch']);

};
